-- database unit test for dbo.AddTask_SP
-- Checking the limit of tasks that can be added by anonymous user
DECLARE @UserID AS [dbo].[LargeKey_UDT] = NEXT VALUE FOR User_Master_SEQ,
		@UserType AS [dbo].[LookupKey_UDT] = 5, 
		@Counter AS INT = 1,
		@Name AS NVARCHAR(500) = 'Anony',
		@ReturnValue AS [dbo].[LargeKey_UDT],
		@Title AS NVARCHAR (100),
		@Priority AS [dbo].[LookupKey_UDT] = 5, 
		@Status AS [dbo].[LookupKey_UDT] = 5, 
		@TaskID AS LargeKey_UDT,
		@Tags AS [dbo].[TaskTag_UDTT];



		INSERT INTO User_Master(
				UM_ID_PK,
				UM_UserType_FK,
				UM_Name)
		VALUES (
		        @UserID,
				@UserType,
				@Name
				);				

WHILE @Counter <= 1000
BEGIN
	SET  @Title =  'Test Task ' + CAST( LEFT(NEWID(), 4 ) AS  VARCHAR);
	INSERT INTO Task_Master(
				TM_ID_PK,
				TM_Title,
				TM_Description,
				TM_UserID_FK,
				TM_Status_FK,
				TM_StartTime,
				TM_EndTime,
				TM_Priority_FK
				)
		VALUES (
				NEXT VALUE FOR Task_Master_Seq,
				@Title,
				NULL,
				@UserID,
				@Status, 
				NULL,
				NULL,
				@Priority
				);

    SET @Counter = @Counter + 1;
END;

EXECUTE  [dbo].[AddTask_SP] @UserID, @Title, NULL, NULL, NULL,@Priority, @Tags;
